$(document).ready(function(){$.getJSON("../data/letters.json",function(o){$.each(o.letters,function(o,t){$(".container").append('<div class="col-md-3 col-lg-2 col-xs-6 letter-item">'+o+'</div><audio class="js-sound"><source src="audio/'+t.audio+'.mp3"></source></audio>')})}),$(".container").on("click",".letter-item",function(){$(".js-sound").trigger("play")}),$("audio").on("play",function(){var o=$(this);$("audio").each(function(t,e){$(e).is(o)||$(e).get(0).pause()})})});