$(document).ready(function(){$.getJSON("../data/letters.json",function(e){$.each(e.letters,function(e,o){$(".container").append('<div class="col-md-3 col-lg-2 col-xs-6 letter-item">'+e+'</div><audio class="js-audio"><source src="audio/'+o.audio+'.mp3"></source></audio>')})});var e=$(".js-audio")[0];$(".container").on("click",".letter-item",function(){alert("playing"+e),e.play()})});